	 * Otherwise the results are fetched from the database the first time, then cached.
	 * Next time the same method is called without $criteria, the cached collection is returned.
	 * If this LogMail is new, it will return
	 * an empty collection or the current collection; the criteria is ignored on a new object.
	 *
	 * @param      Criteria $criteria optional Criteria object to narrow the query
	 * @param      PropelPDO $con optional connection object
	 * @return     PropelCollection|array LogMailAttachment[] List of LogMailAttachment objects
	 * @throws     PropelException
	 */
	public function getLogMailAttachments($criteria = null, PropelPDO $con = null)
	{
		if(null === $this->collLogMailAttachments || null !== $criteria) {
			if ($this->isNew() && null === $this->collLogMailAttachments) {
				// return empty collection
				$this->initLogMailAttachments();
			} else {
				$collLogMailAttachments = LogMailAttachmentQuery::create(null, $criteria)
					->filterByLogMail($this)
					->find($con);
				if (null !== $criteria) {
					return $collLogMailAttachments;
				}
				$this->collLogMailAttachments = $collLogMailAttachments;
			}
		}
		return $this->collLogMailAttachments;
	}

	/**
	 * Returns the number of related LogMailAttachment objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related LogMailAttachment objects.
	 * @throws     PropelException
	 */
	public function countLogMailAttachments(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if(null === $this->collLogMailAttachments || null !== $criteria) {
			if ($this->isNew() && null === $this->collLogMailAttachments) {
				return 0;
			} else {
				$query = LogMailAttachmentQuery::create(null, $criteria);
				if($distinct) {
					$query->distinct();
				}
				return $query
					->filterByLogMail($this)
					->count($con);
			}
		} else {
			return count($this->collLogMailAttachments);
		}
	}

	/**
	 * Method called to associate a LogMailAttachment object to this object
	 * through the LogMailAttachment foreign key attribute.
	 *
	 * @param      LogMailAttachment $l LogMailAttachment
	 * @return     void
	 * @throws     PropelException
	 */
	public function addLogMailAttachment(LogMailAttachment $l)
	{
		if ($this->collLogMailAttachments === null) {
			$this->initLogMailAttachments();
		}
		if (!$this->collLogMailAttachments->contains($l)) { // only add it if the **same** object is not already associated
			$this->collLogMailAttachments[]= $l;
			$l->setLogMail($this);
		}
	}

	/**
	 * Clears the current object and sets all attributes to their default values
	 */
	public function clear()
	{
		$this->id = null;
		$this->username = null;
		$this->body_html = null;
		$this->body_text = null;
		$this->created_at = null;
		$this->subject = null;
		$this->strfrom = null;
		$this->reply_to = null;
		$this->notify_to = null;
		$this->platform = null;
		$this->public_path = null;
		$this->batch_size = null;
		$this->state = null;
		$this->error = null;
		$this->token = null;
		$this->alreadyInSave = false;
		$this->alreadyInValidation = false;
		$this->clearAllReferences();
		$this->applyDefaultValues();
		$this->resetModified();
		$this->setNew(true);
		$this->setDeleted(false);
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collLogMailtos) {
				foreach ((array) $this->collLogMailtos as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collLogMailAttachments) {
				foreach ((array) $this->collLogMailAttachments as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collLogMailtos = null;
		$this->collLogMailAttachments = null;
	}

	/**
	 * Catches calls to virtual methods
	 */
	public function __call($name, $params)
	{
		// symfony_behaviors behavior
		if ($callable = sfMixer::getCallable('BaseLogMail:' . $name))
		{
		  array_unshift($params, $this);
		  return call_user_func_array($callable, $params);
		}

		if (preg_match('/get(\w+)/', $name, $matches)) {
			$virtualColumn = $matches[1];
			if ($this->hasVirtualColumn($virtualColumn)) {
				return $this->getVirtualColumn($virtualColumn);
			}
			// no lcfirst in php<5.3...
			$virtualColumn[0] = strtolower($virtualColumn[0]);
			if ($this->hasVirtualColumn($virtualColumn)) {
				return $this->getVirtualColumn($virtualColumn);
			}
		}
		return parent::__call($name, $params);
	}

} // BaseLogMail
